var counter=0,glider=new Glider(document.querySelector(".glider"),{slidesToShow:1,draggable:!1,arrows:{prev:".glider-prev",next:".glider-next"}});function slideTimer(e,t){var r=setInterval(e,t);this.start=function(){return r||(this.stop(),r=setInterval(e,t)),this},this.stop=function(){return r&&(clearInterval(r),r=null),this},this.reset=function(e=5e3){return t=e,this.stop().start()}}document.querySelector(".glider-next").onclick=incrementCounter,document.querySelector(".glider-prev").onclick=decrementCounter;var initialTimer=new slideTimer(function(){counter>7&&(counter=0),moveItem(counter),counter++},5500);function incrementCounter(){counter<7&&(initialTimer.reset(),counter++)}function decrementCounter(){counter>0&&(initialTimer.reset(),counter--)}function moveItem(e){glider.scrollItem(e)}